'use strict';

var fs = require('fs');
var tracer = require('./logger').getTracer(__filename);
var xsenv = require('@sap/xsenv');

module.exports = {
  getAppEnv: function () {
    var appEnv = {};
    var vcap = process.env.VCAP_APPLICATION;
    if (vcap) {
      try {
        appEnv = JSON.parse(vcap);
      } catch (err) {
        tracer.debug('vcap-utils: Could not parse VCAP_APPLICATION!', err);
      }
    }
    setName(appEnv);
    return appEnv;
  },

  getServiceCredentialsByLabel : function (serviceLabel) {
    var errorOccurred;
    var credentials;
    try {
      credentials = xsenv.cfServiceCredentials({label: serviceLabel});
    } catch (e) {
      errorOccurred = true;
    }
    if (errorOccurred || !credentials) {
      tracer.error('Error during loading of ' + serviceLabel + ' service credentials. ' + serviceLabel + ' service is not bound');
      return null;
    }
    return credentials;
  }
};

function setName(appEnv) {
  if (appEnv.name || appEnv.application_name) {
    return;
  }
  try {
    var packageJsonAsStr = fs.readFileSync('package.json', 'utf8');
    var packageJson = JSON.parse(packageJsonAsStr);
    if (packageJson.name) {
      appEnv.name = packageJson.name;
    }
  } catch (err) {
    tracer.debug('vcap-utils: Could not read from package.json', err);
  }
}
