'use strict';

var _ = require('lodash');


exports.STORAGE = {
  CONSOLE: { name: 'console' }
};

exports.DEFAULT_LEVELS = {
  LOGGER: 'info',
  TRACER: 'error'
};

exports.TRANSPORT_NAMES = {
  LOGGER: 'transport-log-entries',
  TRACER: 'transport-trace-entries'
};

exports.LOG_LEVELS = {
  info: 2,
  warning: 3,
  error: 4,
  fatal: 5
};

exports.LOG_COLORS = {
  info: 'blue',
  warning: 'yellow',
  error: 'red',
  fatal: ['red', 'bold']
};

exports.TRACE_LEVELS = _.extend({
  debug: 0,
  path: 1
}, exports.LOG_LEVELS);

exports.TRACE_COLORS = _.extend({
  debug: 'magenta',
  path: 'cyan'
}, exports.LOG_COLORS);

exports.ALL_LEVELS = exports.TRACE_LEVELS;

exports.MOST_VERBOSE_LEVEL = {
  LOGGER: exports.DEFAULT_LEVELS.LOGGER
};

exports.CONSOLE_TRANSPORT = {
  STD_ERR_LEVELS: Object.keys(exports.TRACE_LEVELS)
};

exports.NEW_LINE_SEPARATOR = '{xsa.newlineSeparator}';
